export default class OpenCageProvider extends AbstractProvider<
  RequestResult,
  RawResult
> {
  searchUrl = 'https://api.opencagedata.com/geocode/v1/json';

  endpoint({ query }: EndpointArgument) {
    const params = typeof query === 'string' ? { q: query } : query;
    params.format = 'json';

    return this.getUrl(this.searchUrl, params);
  }

  parse(response: ParseArgument<RequestResult>): SearchResult<RawResult>[] {
    return response.data.results.map((r) => ({
      x: r.geometry.lng,
      y: r.geometry.lat,
      label: r.formatted,
      bounds: [
        [r.bounds.southwest.lat, r.bounds.southwest.lng], // s, w
        [r.bounds.northeast.lat, r.bounds.northeast.lng], // n, e
      ],
      raw: r,
    }));
  }

  async search(options: SearchArgument): Promise<SearchResult<RawResult>[]> {
    // opencage returns a 400 error when query length < 2
    if (options.query.length < 2) {
      return [];
    }

    return super.search(options);
  }
}
import AbstractProvider, {
  EndpointArgument,
  ParseArgument,
  ProviderOptions,
  SearchResult,
  BoundsTuple,
} from './provider';

export type RequestResult = {
  features: RawResult[];
};
export interface RawResult {
  center: [string, string];
  text: string;
  place_name: string;
  bbox: [string, string, string, string];
}

export type MapBoxProviderOptions = {
  searchUrl?: string;
  reverseUrl?: string;
} & ProviderOptions;

export default class MapBoxProvider extends AbstractProvider<
  RequestResult,
  RawResult
> {
  searchUrl: string;

  constructor(options: MapBoxProviderOptions = {}) {
    super(options);

    const host = 'https://a.tiles.mapbox.com';
    this.searchUrl = options.searchUrl || `${host}/v4/geocode/mapbox.places/`;
  }

  endpoint({ query }: EndpointArgument): string {
    return this.getUrl(`${this.searchUrl}${query}.json`);
  }

  parse(response: ParseArgument<RequestResult>): SearchResult<RawResult>[] {
    const records = Array.isArray(response.data.features)
      ? response.data.features
      : [];

    return records.map((r) => {
      let bounds = null;
      if (r.bbox) {
        bounds = [
          [parseFloat(r.bbox[1]), parseFloat(r.bbox[0])], // s, w
          [parseFloat(r.bbox[3]), parseFloat(r.bbox[2])], // n, e
        ] as BoundsTuple;
      }

      return {
        x: Number(r.center[0]),
        y: Number(r.center[1]),
        label: r.place_name ? r.place_name : r.text,
        bounds,
        raw: r,
      };
    });
  }
}
